mapper.select:
select count(1) from scheduled_record where is_delete = 0 and office_id in ( select t_str as office_role from f_split2(( select r.office_ids from office_area_authority r where r.is_delete = 0 and r.employee_id = #{employeeId} ),',') )
and office_id = #{ officeId }
and bi_id = #{ biId }
and status=#{conferenceStatus}
and employee_name like '%${ employeeName }%'

select sr_id, bi_id, start_time, end_time, meeting_theme, employee_id, employee_name, department_id, department_name, phone, office_id, status from scheduled_record where is_delete = 0 and office_id in ( select t_str as office_role from f_split2(( select r.office_ids from office_area_authority r where r.is_delete = 0 and r.employee_id = #{employeeId} ),',') )
and office_id = #{ officeId }
and bi_id = #{ biId }
and status=#{conferenceStatus}
and employee_name like '%${ employeeName }%'
order by status, start_time asc OFFSET #{ pageNo } ROW FETCH NEXT #{ pageSize } ROW ONLY

	   DECLARE @srId VARCHAR ( 200 );
UPDATE scheduled_record
SET @srId = sr_id,
STATUS = 0,
update_time = CURRENT_TIMESTAMP
WHERE
	bi_id = #{ biId }

	AND (
	employee_id = #{ employeeId } or  employee_id in (SELECT
att.employee_id
FROM
scheduled_record sr
LEFT JOIN attendees att ON att.sr_id = sr.sr_id
WHERE
sr.bi_id = #{ biId }

AND sr.STATUS =- 1
AND sr.is_delete = 0
AND sr.start_time BETWEEN DATEADD ( MINUTE,- 15, GETDATE ( ) )
	AND DATEADD ( MINUTE,+ 15, GETDATE ( ) );

)
)
AND STATUS =- 1
AND is_delete = 0
AND start_time BETWEEN DATEADD ( MINUTE,- 15, GETDATE ( ) )
AND DATEADD ( MINUTE,+ 15, GETDATE ( ) );
SELECT
@srId;
